import React, { useState } from 'react';
import './Navbar.css';
import logo from '../assets/frontend_assets/PetShop3.png';
import cart_icon from '../assets/frontend_assets/cart-icon.png';
import user_icon from '../assets/frontend_assets/user_account.png';
import { Link } from 'react-router-dom';

const Navbar = () => {
    const [menu, setMenu] = useState("shop")

    return (
        <div className='banner'>
            <div className='navbar'>
                <div className="nav-logo">
                    <img src={logo} alt="Pet Shop" />
                </div>
                <div className="nav-cart-user">
                    <ul className="nav-menu">
                        <li onClick={()=> {setMenu("shop")}}><Link style={{textDecoration:'none'}} to='/'>חנות</Link></li>
                        <li onClick={()=> {setMenu("dogs")}}><Link style={{textDecoration:'none'}} to='/dogs'>כלבים</Link></li>
                        <li onClick={()=> {setMenu("cats")}}><Link style={{textDecoration:'none'}} to='/cats'>חתולים</Link></li>
                        <li onClick={()=> {setMenu("birds")}}><Link style={{textDecoration:'none'}} to='/birds'>ציפורים</Link></li>
                    </ul>
                    <img src={user_icon} alt="User" />
                    <div className="cart-count">0</div>
                    <Link to='/cart'><img src={cart_icon} alt="Cart" /></Link>
                </div>
            </div>
        </div>
    );
};

export default Navbar;
