import React, { useState, forwardRef, useImperativeHandle } from 'react';
import { TextField, Box } from '@mui/material';

const DeliveryInfo = forwardRef((_, ref) => {
    const [formData, setFormData] = useState({
        name: '',
        phone: '',
        email: '',
        city: '',
        address: '',
        zipCode: '',
        instructions: '',
    });

    const [errors, setErrors] = useState({
        name: false,
        phone: false,
        email: false,
        city: false,
        address: false,
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
        if (errors[name]) {
            setErrors({ ...errors, [name]: !value.trim() });
        }
    };

    const validateFields = () => {
        const requiredFields = ['name', 'phone', 'email', 'city', 'address'];
        const newErrors = requiredFields.reduce((acc, field) => {
            acc[field] = !formData[field].trim();
            return acc;
        }, {});
        setErrors(newErrors);
        return !Object.values(newErrors).some((error) => error);
    };

    // Expose validation function to parent via ref
    useImperativeHandle(ref, () => ({
        validate: validateFields,
    }));

    const textFieldStyles = {
        '& .MuiInputLabel-root': {
            textAlign: 'right',
            right: 0,
            transformOrigin: 'top right',
        },
        '& .MuiInputBase-input': {
            textAlign: 'right',
        },
        '& .Mui-error': {
            borderColor: 'red',
        },
    };

    return (
        <Box
            className="checkout-customer-details"
            component="form"
            sx={{
                display: 'flex',
                flexDirection: 'column',
                gap: 2,
                width: '80%',
                margin: '0 auto',
                mb: 10,
            }}
        >
            <TextField
                name="name"
                label="שם מלא"
                variant="standard"
                value={formData.name}
                onChange={handleInputChange}
                fullWidth
                sx={textFieldStyles}
                error={errors.name}
                helperText={errors.name ? 'שדה זה נדרש' : ''}
            />
            <TextField
                name="phone"
                label="טלפון"
                variant="standard"
                value={formData.phone}
                onChange={handleInputChange}
                fullWidth
                sx={textFieldStyles}
                error={errors.phone}
                helperText={errors.phone ? 'שדה זה נדרש' : ''}
            />
            <TextField
                name="email"
                label="דוא״ל"
                variant="standard"
                value={formData.email}
                onChange={handleInputChange}
                fullWidth
                sx={textFieldStyles}
                error={errors.email}
                helperText={errors.email ? 'שדה זה נדרש' : ''}
            />
            <TextField
                name="city"
                label="עיר"
                variant="standard"
                value={formData.city}
                onChange={handleInputChange}
                fullWidth
                sx={textFieldStyles}
                error={errors.city}
                helperText={errors.city ? 'שדה זה נדרש' : ''}
            />
            <TextField
                name="address"
                label="כתובת"
                variant="standard"
                value={formData.address}
                onChange={handleInputChange}
                fullWidth
                sx={textFieldStyles}
                error={errors.address}
                helperText={errors.address ? 'שדה זה נדרש' : ''}
            />
            <TextField
                name="zipCode"
                label="מיקוד"
                variant="standard"
                value={formData.zipCode}
                onChange={handleInputChange}
                fullWidth
                sx={textFieldStyles}
            />
            <TextField
                name="instructions"
                label="הוראות"
                variant="standard"
                value={formData.instructions}
                onChange={handleInputChange}
                multiline
                rows={4}
                fullWidth
                sx={textFieldStyles}
            />
        </Box>
    );
});

export default DeliveryInfo;
