import React, { useState } from 'react';
import { TextField, Button, Box } from '@mui/material';
import './Checkout.css';

const Checkout = ({ totalCartValue, isCartEmpty, navigateToOrder }) => {
    const [formData, setFormData] = useState({
        name: '',
        phone: '',
        email: '',
        city: '',
        address: '',
        zipCode: '',
        instructions: '',
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleFormSubmit = () => {
        if (!isCartEmpty) {
            console.log('User Delivery Details:', formData);
            navigateToOrder();
        }
    };

    return (
        <div className="checkout-section">
            <h2>פרטי משלוח</h2>
            <Box 
                className="checkout-customer-details" 
                component="form" 
                sx={{ display: 'flex', flexDirection: 'column', gap: 2, width: '100%' }}
            >
                <TextField
    name="name"
    label="שם מלא"
    variant="standard"
    value={formData.name}
    onChange={handleInputChange}
    fullWidth
    sx={{
        '& .MuiInputLabel-root': {
            textAlign: 'right',
            right: 0,
            transformOrigin: 'top right',
        },
        '& .MuiInputBase-input': {
            textAlign: 'right',
        },
    }}
/>

                <TextField
                    name="phone"
                    label="טלפון"
                    variant="standard"
                    value={formData.phone}
                    onChange={handleInputChange}
                    InputLabelProps={{ style: { textAlign: 'right', direction: 'rtl' } }}
                    fullWidth
                />
                <TextField
                    name="email"
                    label="דואר אלקטרוני"
                    variant="standard"
                    value={formData.email}
                    onChange={handleInputChange}
                    InputLabelProps={{ style: { textAlign: 'right', direction: 'rtl' } }}
                    fullWidth
                />
                <TextField
                    name="city"
                    label="עיר"
                    variant="standard"
                    value={formData.city}
                    onChange={handleInputChange}
                    InputLabelProps={{ style: { textAlign: 'right', direction: 'rtl' } }}
                    fullWidth
                />
                <TextField
                    name="address"
                    label="כתובת"
                    variant="standard"
                    value={formData.address}
                    onChange={handleInputChange}
                    InputLabelProps={{ style: { textAlign: 'right', direction: 'rtl' } }}
                    fullWidth
                />
                <TextField
                    name="zipCode"
                    label="מיקוד"
                    variant="standard"
                    value={formData.zipCode}
                    onChange={handleInputChange}
                    InputLabelProps={{ style: { textAlign: 'right', direction: 'rtl' } }}
                    fullWidth
                />
                <TextField
                    name="instructions"
                    label="הוראות מיוחדות"
                    variant="standard"
                    value={formData.instructions}
                    onChange={handleInputChange}
                    InputLabelProps={{ style: { textAlign: 'right', direction: 'rtl' } }}
                    multiline
                    rows={4}
                    fullWidth
                />
            </Box>
            <Button
                className="checkout-button"
                onClick={handleFormSubmit}
                disabled={isCartEmpty}
                variant="contained"
                color="primary"
                sx={{ marginTop: 2 }}
            >
                המשך לתשלום
            </Button>
        </div>
    );
};

export default Checkout;
