import React, { useState } from 'react';
import { TextField, Button, Box } from '@mui/material';
import './Checkout.css';

const Checkout = ({ totalCartValue, isCartEmpty, navigateToOrder }) => {
    const [formData, setFormData] = useState({
        name: '',
        phone: '',
        email: '',
        city: '',
        address: '',
        zipCode: '',
        instructions: '',
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleFormSubmit = () => {
        if (!isCartEmpty) {
            console.log('User Delivery Details:', formData);
            navigateToOrder();
        }
    };

    return (
        <div className="checkout-section">
            <h2>פרטי משלוח</h2>
            <form className="delivery-form">
                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                    <TextField
                        label="שם מלא"
                        variant="standard"
                        name="name"
                        value={formData.name}
                        onChange={handleInputChange}
                        fullWidth
                        required
                        InputLabelProps={{
                            style: { textAlign: 'right' },
                        }}
                    />

                    <TextField
                        label="טלפון"
                        variant="standard"
                        name="phone"
                        value={formData.phone}
                        onChange={handleInputChange}
                        fullWidth
                        required
                        InputLabelProps={{
                            style: { textAlign: 'right' },
                        }}
                    />

                    <TextField
                        label="אימייל"
                        variant="standard"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        fullWidth
                        required
                        InputLabelProps={{
                            style: { textAlign: 'right' },
                        }}
                    />

                    <TextField
                        label="עיר"
                        variant="standard"
                        name="city"
                        value={formData.city}
                        onChange={handleInputChange}
                        fullWidth
                        required
                        InputLabelProps={{
                            style: { textAlign: 'right' },
                        }}
                    />

                    <TextField
                        label="כתובת"
                        variant="standard"
                        name="address"
                        value={formData.address}
                        onChange={handleInputChange}
                        fullWidth
                        required
                        InputLabelProps={{
                            style: { textAlign: 'right' },
                        }}
                    />

                    <TextField
                        label="מיקוד"
                        variant="standard"
                        name="zipCode"
                        value={formData.zipCode}
                        onChange={handleInputChange}
                        fullWidth
                        required
                        InputLabelProps={{
                            style: { textAlign: 'right' },
                        }}
                    />

                    <TextField
                        label="הוראות משלוח (לא חובה)"
                        variant="standard"
                        name="instructions"
                        value={formData.instructions}
                        onChange={handleInputChange}
                        fullWidth
                        multiline
                        rows={4}
                        InputLabelProps={{
                            style: { textAlign: 'right' },
                        }}
                    />
                </Box>
            </form>

            <Button
                className="checkout-button"
                onClick={handleFormSubmit}
                disabled={isCartEmpty}
                variant="contained"
                color="primary"
            >
                המשך לתשלום
            </Button>
        </div>
    );
};

export default Checkout;
