import React, { useState, forwardRef, useImperativeHandle } from 'react';
import { TextField, Box } from '@mui/material';

const DeliveryInfo = forwardRef((_, ref) => {
    const [formData, setFormData] = useState({
        name: '',
        phone: '',
        email: '',
        city: '',
        address: '',
        zipCode: '',
        instructions: '',
    });

    const [errors, setErrors] = useState({
        name: '',
        phone: '',
        email: '',
        city: '',
        address: '',
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;

        setFormData({ ...formData, [name]: value });

        // Clear error messages while typing
        if (errors[name]) {
            setErrors({ ...errors, [name]: '' });
        }
    };

    const validateFields = () => {
        const newErrors = {};

        // Required fields
        if (!formData.name.trim()) newErrors.name = 'נא להזין שם מלא';
        if (!formData.city.trim()) newErrors.city = 'נא להזין עיר';
        if (!formData.address.trim()) newErrors.address = 'נא להזין כתובת';

        // Email validation
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!formData.email.trim()) {
            newErrors.email = 'נא להזין כתובת דוא״ל';
        } else if (!emailRegex.test(formData.email)) {
            newErrors.email = 'נא להזין דוא״ל חוקי';
        }

        // Phone number validation
        const phoneRegex = /^[0-9]+$/;
        if (!formData.phone.trim()) {
            newErrors.phone = 'נא להזין מספר טלפון';
        } else if (!phoneRegex.test(formData.phone)) {
            newErrors.phone = 'מספר טלפון צריך להכיל רק ספרות';
        }

        setErrors(newErrors);

        // Return false if there are any errors
        return Object.keys(newErrors).length === 0;
    };

    // Expose validation function to parent via ref
    useImperativeHandle(ref, () => ({
        validate: validateFields,
    }));

    const textFieldStyles = {
        '& .MuiInputLabel-root': {
            textAlign: 'right',
            right: 0,
            transformOrigin: 'top right',
        },
        '& .MuiInputBase-input': {
            textAlign: 'right',
        },
    };

    return (
        <Box
            className="checkout-customer-details"
            component="form"
            sx={{
                display: 'flex',
                flexDirection: 'column',
                gap: 2,
                width: '80%',
                margin: '0 auto',
                mb: 10,
            }}
        >
            <TextField
                name="name"
                label="שם מלא"
                variant="standard"
                value={formData.name}
                onChange={handleInputChange}
                fullWidth
                sx={textFieldStyles}
                error={!!errors.name}
                helperText={errors.name}
            />
            <TextField
                name="phone"
                label="טלפון"
                variant="standard"
                value={formData.phone}
                onChange={handleInputChange}
                fullWidth
                sx={textFieldStyles}
                error={!!errors.phone}
                helperText={errors.phone}
            />
            <TextField
                name="email"
                label="דוא״ל"
                variant="standard"
                value={formData.email}
                onChange={handleInputChange}
                fullWidth
                sx={textFieldStyles}
                error={!!errors.email}
                helperText={errors.email}
            />
            <TextField
                name="city"
                label="עיר"
                variant="standard"
                value={formData.city}
                onChange={handleInputChange}
                fullWidth
                sx={textFieldStyles}
                error={!!errors.city}
                helperText={errors.city}
            />
            <TextField
                name="address"
                label="כתובת"
                variant="standard"
                value={formData.address}
                onChange={handleInputChange}
                fullWidth
                sx={textFieldStyles}
                error={!!errors.address}
                helperText={errors.address}
            />
            <TextField
                name="zipCode"
                label="מיקוד"
                variant="standard"
                value={formData.zipCode}
                onChange={handleInputChange}
                fullWidth
                sx={textFieldStyles}
            />
            <TextField
                name="instructions"
                label="הוראות"
                variant="standard"
                value={formData.instructions}
                onChange={handleInputChange}
                multiline
                rows={4}
                fullWidth
                sx={textFieldStyles}
            />
        </Box>
    );
});

export default DeliveryInfo;
