import React, { useContext, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Carousel.css';
import { ShopContext } from '../../context/ShopContext';

const Carousel = () => {
    const [brands] = useContext(ShopContext)

    useEffect(() => {
        const fetchBrands = async () => {
            try {
                const response = await fetch('http://localhost:4000/allbrands'); // Update the API URL as needed
                const data = await response.json();
                setBrands(data); // Assuming the response is an array of brand objects
                setLoading(false);
            } catch (error) {
                console.error('Error fetching brands:', error);
                setLoading(false);
            }
        };

        fetchBrands();
    }, []);

    if (loading) {
        return <div style={{textAlign: 'center'}}>Loading...</div>;
    }

    return (
        <div className='carousel'>
            <div className="logos">
                {brands.map((brand, index) => (
                    <Link key={index} to={`/brand/${brand.name}`} onClick={() => window.scrollTo(0, 0)}>
                        <img src={brand.logo} alt={brand.name} />
                    </Link>
                ))}
            </div>
            <div className="logos">
                {brands.map((brand, index) => (
                    <Link key={index} to={`/brand/${brand.name}`} onClick={() => window.scrollTo(0, 0)}>
                        <img src={brand.logo} alt={brand.name} />
                    </Link>
                ))}
            </div>
        </div>
    );
};

export default Carousel;
