import React from 'react';
import { TextField, Button, Box } from '@mui/material';

const textFieldStyles = {


    '& .MuiInputLabel-root': {
        textAlign: 'right',
        right: 0,
        transformOrigin: 'top right',
    },
    '& .MuiInputBase-input': {
        textAlign: 'right',
    },
};

const DeliveryInfo = () => {
    return (
        <div>
            <Box 
                className="checkout-customer-details" 
                component="form" 
                sx={{ display: 'flex', flexDirection: 'column', gap: 2, width: '80%', margin: '0 auto', mb: 10 }}
            >
                <TextField
                    name="name"
                    label="שם מלא"
                    variant="standard"
                    value={formData.name}
                    onChange={handleInputChange}
                    fullWidth
                    sx={textFieldStyles}
                />
                <TextField
                    name="phone"
                    label="טלפון"
                    variant="standard"
                    value={formData.phone}
                    onChange={handleInputChange}
                    fullWidth
                    sx={textFieldStyles}
                />
                <TextField
                    name="email"
                    label="דוא״ל"
                    variant="standard"
                    value={formData.email}
                    onChange={handleInputChange}
                    fullWidth
                    sx={textFieldStyles}
                />
                <TextField
                    name="city"
                    label="עיר"
                    variant="standard"
                    value={formData.city}
                    onChange={handleInputChange}
                    fullWidth
                    sx={textFieldStyles}
                />
                <TextField
                    name="address"
                    label="כתובת"
                    variant="standard"
                    value={formData.address}
                    onChange={handleInputChange}
                    fullWidth
                    sx={textFieldStyles}
                />
                <TextField
                    name="zipCode"
                    label="מיקוד"
                    variant="standard"
                    value={formData.zipCode}
                    onChange={handleInputChange}
                    fullWidth
                    sx={textFieldStyles}
                />
                <TextField
                    name="instructions"
                    label="הוראות"
                    variant="standard"
                    value={formData.instructions}
                    onChange={handleInputChange}
                    multiline
                    rows={4}
                    fullWidth
                    sx={textFieldStyles}
                />
            </Box>
        </div>
    );
};

export default DeliveryInfo;
