// CartPage.js
import React, { useContext } from 'react';
import './CartPage.css';
import { ShopContext } from '../../context/ShopContext';

const CartPage = () => {
    const { cartItems, all_products, removeFromCart } = useContext(ShopContext);

    // Calculate total price for each product in the cart
    const getTotalPrice = (price, quantity) => price * quantity;

    // Filter products in the cart
    const cartProducts = all_products.filter(product => cartItems[product.id] > 0);

    // Calculate total cart value
    const totalCartValue = cartProducts.reduce((total, product) => {
        return total + getTotalPrice(product.price, cartItems[product.id]);
    }, 0);

    return (
        <div className="cart-page">
            <div className="cart-items">
                {cartProducts.length === 0 ? (
                    <p>Your cart is empty.</p>
                ) : (
                    cartProducts.map((product) => (
                        <div key={product.id} className="cart-item">
                            <img src={product.image} alt={product.name} className="cart-item-image" />
                            <div className="cart-item-details">
                                <h2>{product.name}</h2>
                                <p>Price: ${product.price.toFixed(2)}</p>
                                <p>Quantity: {cartItems[product.id]}</p>
                                <p>Total: ${getTotalPrice(product.price, cartItems[product.id]).toFixed(2)}</p>
                                <button
                                    className="delete-button"
                                    onClick={() => removeFromCart(product.id)}
                                >
                                    Remove
                                </button>
                            </div>
                        </div>
                    ))
                )}
            </div>
            <div className="checkout-section">
                <h2>Checkout</h2>
                <p>Total: ${totalCartValue.toFixed(2)}</p>
                <button className="checkout-button">Proceed to Checkout</button>
            </div>
        </div>
    );
};

export default Cart;
