import React, { useContext, useState, useEffect } from 'react';
import './css/ShopCategory.css';
import { ShopContext } from '../context/ShopContext';
import Item from '../components/item/Item';

const ShopCategory = (props) => {
  const { all_products } = useContext(ShopContext);

  const [searchTerm, setSearchTerm] = useState(''); // State for search term
  const [visibleProducts, setVisibleProducts] = useState(12); // State for number of visible products

  // Filter products based on search term and category
  const filteredProducts = all_products.filter((item) => {
    return (
      (props.category === item.category) && // Ensure the product is in the current category
      (item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.brand.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.id.toString().includes(searchTerm)) // Match by name, brand, or ID
    );
  });

  // Handle infinite scrolling
  const handleScroll = () => {
    if (window.innerHeight + document.documentElement.scrollTop >= document.documentElement.offsetHeight - 50) {
      // Load 12 more products if user scrolls near the bottom
      setVisibleProducts((prevVisibleProducts) => prevVisibleProducts + 12);
    }
  };

  useEffect(() => {
    // Add scroll event listener
    window.addEventListener('scroll', handleScroll);
    return () => {
      // Cleanup scroll event listener on unmount
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className='category'>
      <img className='category-banner' src={props.banner} alt="" />
      {/* Search Bar */}
      <div className="search-bar">
          <input
            type="text"
            placeholder="Search by ID, Name, or Brand..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)} // Update search term
            className="search-input"
          />
        </div>
      <div className="content-container">

        {/* Products */}
        <div className="category-products">
          {filteredProducts.slice(0, visibleProducts).map((item, i) => (
            <Item
              key={i}
              id={item.id}
              name={item.name}
              image={item.image}
              price={item.price}
              brand={item.brand}
            />
          ))}

          {/* No results message */}
          {filteredProducts.length === 0 && <p className="no-results">No products match your search.</p>}
        </div>
      </div>
    </div>
  );
};

export default ShopCategory;
