import React, { useContext, useState, useEffect } from 'react';
import './css/ShopCategory.css';
import { ShopContext } from '../context/ShopContext';
import Item from '../components/item/Item';

const ShopCategory = (props) => {
  const { all_products } = useContext(ShopContext);
  const [searchTerm, setSearchTerm] = useState(''); 
  const [visibleProducts, setVisibleProducts] = useState(12); 
  const [isLoading, setIsLoading] = useState(false); 

  const filteredProducts = all_products.filter((item) => {
    return (
      props.category === item.category &&
      (!props.type || props.type === item.product_type) && // Match type only if it exists
      (
        item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.brand.toLowerCase().includes(searchTerm.toLowerCase()) ||
        item.id.toString().includes(searchTerm) ||
        (item.description && item.description.toLowerCase().includes(searchTerm.toLowerCase())) // Check for description
      )
    );
  });

  const handleScroll = () => {
    if (
      window.innerHeight + document.documentElement.scrollTop >=
        document.documentElement.offsetHeight - 50 &&
      !isLoading &&
      visibleProducts < filteredProducts.length
    ) {
      setIsLoading(true); 
      setTimeout(() => {
        setVisibleProducts((prev) => prev + 12); 
        setIsLoading(false); 
      }, 500); 
    }
  };
  useEffect(() => {
    const handleParallax = () => {
      const banner = document.querySelector('.category-banner');
      if (banner) {
        const offset = window.scrollY * 0.5; // Adjust speed by changing multiplier
        banner.style.backgroundPosition = `center ${offset}px`;
      }
    };

    window.addEventListener('scroll', handleParallax);

    return () => window.removeEventListener('scroll', handleParallax);
  }, []);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [visibleProducts, isLoading, filteredProducts]);

  return (
    <div className="category">
      <div className="category-banner" style={{ backgroundImage: `url(${props.banner})` }}></div>

      <div className="search-bar">
        <input
          type="text"
          placeholder="חפש לפי מזהה, שם או מותג..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
        <div className="product-count">
          {filteredProducts.length} מוצרים
        </div>
      </div>
      <div className="content-container">
        <div className="category-products">
          {filteredProducts.length > 0 ? (
            filteredProducts.slice(0, visibleProducts).map((item) => (
              item.available && (
                <Item
                  key={item.id}
                  id={item.id}
                  brand={item.brand}
                  name={item.name}
                  image={item.image}
                  price={item.price}
                  discount_type={item.discount_type}
                  on_discount={item.on_discount}
                />
              )
            ))
          ) : (
            <p className="no-results">לא נמצאו מוצרים התואמים לחיפוש שלך.</p>
          )}
        </div>

        {isLoading && <p className="loading-message">טעינת מוצרים נוספים...</p>}
      </div>
    </div>
  );
};

export default ShopCategory;
