import React, { useContext, useState } from 'react';
import './DisplayProduct.css';
import { ShopContext } from '../../context/ShopContext';

const DisplayProduct = (props) => {
    const { product } = props;
    const { addToCart } = useContext(ShopContext);
    const [quantity, setQuantity] = useState(1);
    const [showBadge, setShowBadge] = useState(false); // For the red circle animation

    const handleQuantityChange = (e) => {
        const value = e.target.value;
        if (value === "") {
            setQuantity(""); // Set quantity to an empty string to keep the input field blank
        } else {
            setQuantity(Math.max(0, parseInt(value, 10) || 0));
        }
    };

    const increaseQuantity = () => {
        setQuantity((prevQuantity) => (prevQuantity ? prevQuantity + 1 : 1));
    };

    const decreaseQuantity = () => {
        setQuantity((prevQuantity) => (prevQuantity > 0 ? prevQuantity - 1 : 1));
    };

    const handleAddToCart = () => {
        const quantityToAdd = quantity === "" ? 0 : quantity; // Default to 0 if input is empty
        if (quantityToAdd > 0) {
            addToCart(product.id, quantityToAdd);
            setQuantity(1); // Reset quantity to 1 after adding to cart

            // Trigger animation for the badge
            setShowBadge(true);
            setTimeout(() => {
                setShowBadge(false); // Hide the badge after the animation completes
            }, 1000); // Match the animation duration in CSS
        }
    };

    return (
        <div className='productdisplay'>
            <div className="display-left">
                <div className="product-top">
                    <h1>{product.name}</h1>
                    <p>{product.description}</p>
                </div>
                <hr />
                <div className="product-bottom">
                    <p>â‚ª{product.price}</p>
                    <div className="add-product">
                        <button onClick={handleAddToCart} disabled={quantity <= 0 || quantity === ""}>
                            ADD TO CART
                        </button>
                        <div className="product-quantity">
                            <span onClick={increaseQuantity}>+</span>
                            <input
                                className='counter'
                                type="number"
                                value={quantity}
                                onChange={handleQuantityChange}
                                min="0"
                            />
                            <span onClick={decreaseQuantity}>-</span>
                        </div>
                    </div>
                </div>
            </div>
            <div className="display-right">
                <img src={product.image} alt={product.name} />
            </div>

            {/* Red badge that animates to the cart */}
            {showBadge && (
                <div className="cart-badge">
                    {quantity} {/* The quantity being added */}
                </div>
            )}
        </div>
    );
};

export default DisplayProduct;
