import React, { useState } from 'react';
import './Checkout.css';

const Checkout = ({ totalCartValue, isCartEmpty, navigateToOrder }) => {
    const [formData, setFormData] = useState({
        name: '',
        phone: '',
        email: '',
        city: '',
        address: '',
        zipCode: '',
        instructions: '',
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleFormSubmit = () => {
        if (!isCartEmpty) {
            console.log('User Delivery Details:', formData);
            navigateToOrder();
        }
    };

    return (
        <div className="checkout-section">
            <h2>תשלום</h2>
            <p>סה"כ לתשלום: ₪{totalCartValue.toFixed(2)}</p>

            <form className="delivery-form">
                <label>
                    שם מלא:
                    <input
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleInputChange}
                        placeholder="הזן את שמך"
                        required
                    />
                </label>

                <label>
                    טלפון:
                    <input
                        type="tel"
                        name="phone"
                        value={formData.phone}
                        onChange={handleInputChange}
                        placeholder="הזן את מספר הטלפון"
                        required
                    />
                </label>

                <label>
                    אימייל:
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        placeholder="הזן את כתובת המייל"
                        required
                    />
                </label>

                <label>
                    עיר:
                    <input
                        type="text"
                        name="city"
                        value={formData.city}
                        onChange={handleInputChange}
                        placeholder="הזן את שם העיר"
                        required
                    />
                </label>

                <label>
                    כתובת:
                    <input
                        type="text"
                        name="address"
                        value={formData.address}
                        onChange={handleInputChange}
                        placeholder="הזן את כתובת המשלוח"
                        required
                    />
                </label>

                <label>
                    מיקוד:
                    <input
                        type="text"
                        name="zipCode"
                        value={formData.zipCode}
                        onChange={handleInputChange}
                        placeholder="הזן את המיקוד"
                        required
                    />
                </label>

                <label>
                    הוראות משלוח (לא חובה):
                    <textarea
                        name="instructions"
                        value={formData.instructions}
                        onChange={handleInputChange}
                        placeholder="הזן הוראות מיוחדות לשליח"
                    />
                </label>
            </form>

            <button
                className="checkout-button"
                onClick={handleFormSubmit}
                disabled={isCartEmpty}
            >
                המשך לתשלום
            </button>
        </div>
    );
};

export default Checkout;
