import React, { createContext, useState, useEffect } from "react";

export const ShopContext = createContext(null);

const ShopContextProvider = (props) => {
    const [products, setProducts] = useState([]); // State to store fetched product data
    //const [brands, setBrands] = useState([])
    const [cartItems, setCartItems] = useState(() => {
        // Load initial cart data from localStorage or initialize with empty object
        const savedCart = localStorage.getItem("cartItems");
        return savedCart ? JSON.parse(savedCart) : {};
    });

    // Fetch product data from the backend
    useEffect(() => {
        const fetchProducts = async () => {
            try {
              const response = await fetch("http://localhost:4000/allproducts"); 
              const data = await response.json();
              setProducts(data); 
            } catch (error) {
              console.error("Error fetching products:", error);
            }
          };
        fetchProducts();
    }, []);

    // Fetch brand data 
    useEffect(() => {
        const fetchBrands = async () => {
            try {
                const response = await fetch('http://localhost:4000/allbrands'); // Update the API URL as needed
                const data = await response.json();
                setBrands(data); // Assuming the response is an array of brand objects
                setLoading(false);
            } catch (error) {
                console.error('Error fetching brands:', error);
            }
        };

        fetchBrands();
    }, []);

    // Sync cart data with localStorage whenever cartItems changes
    useEffect(() => {
        localStorage.setItem("cartItems", JSON.stringify(cartItems));
    }, [cartItems]);

    const addToCart = (itemId, quantity = 1) => {
        setCartItems((prev) => ({ ...prev, [itemId]: (prev[itemId] || 0) + quantity }));
    };

    const removeFromCart = (itemId) => {
        setCartItems((prev) => ({
            ...prev,
            [itemId]: Math.max((prev[itemId] || 0) - 1, 0),
        }));
    };

    const updateCartQuantity = (itemId, newQuantity) => {
        setCartItems((prev) => ({ ...prev, [itemId]: newQuantity }));
    };

    const clearItemFromCart = (itemId) => {
        setCartItems((prev) => ({ ...prev, [itemId]: 0 }));
    };

    const getTotalCartValue = () => {
        return products.reduce((total, product) => {
            const quantity = cartItems[product.id] || 0;
            return total + product.price * quantity;
        }, 0);
    };

    const getTotalItems = () => {
        return Object.values(cartItems).reduce((total, quantity) => total + quantity, 0);
    };

    const clearAllItemsFromCart = () => {
        setCartItems({});
    };

    const contextValue = {
        all_products: products, // Renamed to match usage in ShopCategory.jsx
        cartItems,
        addToCart,
        removeFromCart,
        updateCartQuantity,
        clearItemFromCart,
        getTotalCartValue,
        getTotalItems,
        clearAllItemsFromCart,
    };

    return (
        <ShopContext.Provider value={contextValue}>
            {props.children}
        </ShopContext.Provider>
    );
};

export default ShopContextProvider;
