import React, { useContext, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Navbar.css';
import logo from '../assets/frontend_assets/PetShop3.png';
import cart_icon from '../assets/frontend_assets/cart-icon.png';
import user_icon from '../assets/frontend_assets/user_account.png';
import { ShopContext } from '../../context/ShopContext';

const Navbar = () => {
    const [menu, setMenu] = useState("shop");
    const location = useLocation();

    // Only apply the banner class on the home page
    const bannerClass = location.pathname === '/' ? 'banner' : '';
    const {getTotalItems} = useContext(ShopContext)

    return (
        <div className={bannerClass}>
            <div className='navbar'>
                <div className="nav-logo">
                    <img src={logo} alt="Pet Shop" />
                </div>
                <div className="nav-cart-user">
                    <ul className="nav-menu">
                        <li onClick={() => setMenu("shop")}>
                            <Link style={{ textDecoration: 'none' }} to='/'>חנות</Link>
                        </li>
                        <li onClick={() => setMenu("dogs")}>
                            <Link style={{ textDecoration: 'none' }} to='/dogs'>כלבים</Link>
                        </li>
                        <li onClick={() => setMenu("cats")}>
                            <Link style={{ textDecoration: 'none' }} to='/cats'>חתולים</Link>
                        </li>
                        <li onClick={() => setMenu("birds")}>
                            <Link style={{ textDecoration: 'none' }} to='/birds'>ציפורים</Link>
                        </li>
                    </ul>
                    <img src={user_icon} alt="User" />
                    <div className="cart-count">{}</div>
                    <Link to='/cart'>
                        <img src={cart_icon} alt="Cart" />
                    </Link>
                </div>
            </div>
        </div>
    );
};

export default Navbar;
