import React, { useContext, useState } from "react";
import { ShopContext } from "../context/ShopContext";
import "./css/";

const Order = () => {
    const { cartItems, all_products, getTotalCartValue, clearItemFromCart } = useContext(ShopContext);
    const [orderDetails, setOrderDetails] = useState({
        name: "",
        phone: "",
        address: "",
        email: "",
    });

    const cartProducts = all_products.filter((product) => cartItems[product.id] > 0);
    const totalPrice = getTotalCartValue();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setOrderDetails((prevDetails) => ({
            ...prevDetails,
            [name]: value,
        }));
    };

    const handlePlaceOrder = () => {
        if (!orderDetails.name || !orderDetails.phone || !orderDetails.address || !orderDetails.email) {
            alert("Please fill out all the required fields!");
            return;
        }

        // TODO: Replace this with a backend API call to save the order
        console.log("Order Placed:", { orderDetails, cartProducts, totalPrice });

        alert("Order placed successfully!");
        clearItemFromCart(); // Clear the cart after placing the order
    };

    return (
        <div className="order-page">
            <h2>Complete Your Order</h2>

            {/* Contact Information Form */}
            <div className="contact-form">
                <h3>Contact Information</h3>
                <label>
                    Name:
                    <input
                        type="text"
                        name="name"
                        value={orderDetails.name}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Phone:
                    <input
                        type="tel"
                        name="phone"
                        value={orderDetails.phone}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Address:
                    <textarea
                        name="address"
                        value={orderDetails.address}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Email:
                    <input
                        type="email"
                        name="email"
                        value={orderDetails.email}
                        onChange={handleChange}
                        required
                    />
                </label>
            </div>

            {/* Order Summary */}
            <div className="order-summary">
                <h3>Order Summary</h3>
                {cartProducts.map((product) => (
                    <div key={product.id} className="summary-item">
                        <p>
                            {product.name} x {cartItems[product.id]}
                        </p>
                        <p>₪{(product.price * cartItems[product.id]).toFixed(2)}</p>
                    </div>
                ))}
                <p className="total">Total: ₪{totalPrice.toFixed(2)}</p>
            </div>

            {/* Payment Section */}
            <div className="payment-section">
                <h3>Payment Method</h3>
                <label>
                    <input type="radio" name="payment" value="credit" defaultChecked /> Credit/Debit Card
                </label>
                <label>
                    <input type="radio" name="payment" value="paypal" /> PayPal
                </label>
                <label>
                    <input type="radio" name="payment" value="cash" /> Cash on Delivery
                </label>
            </div>

            {/* Place Order Button */}
            <button className="place-order-button" onClick={handlePlaceOrder}>
                Place Order
            </button>
        </div>
    );
};

export default Order;
