import React, { useState } from 'react';
import { TextField, Button, Box } from '@mui/material';
import './Checkout.css';

const Checkout = ({ totalCartValue, isCartEmpty, navigateToOrder }) => {
    const [formData, setFormData] = useState({
        name: '',
        phone: '',
        email: '',
        city: '',
        address: '',
        zipCode: '',
        instructions: '',
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleFormSubmit = () => {
        if (!isCartEmpty) {
            console.log('User Delivery Details:', formData);
            navigateToOrder();
        }
    };

    // Shared sx styles for all TextFields
    const textFieldStyles = {
        '& .MuiInputLabel-root': {
            textAlign: 'right',
            right: 0,
            transformOrigin: 'top right',
        },
        '& .MuiInputBase-input': {
            textAlign: 'right',
        },
    };

    return (
        <div className="checkout-section">
            <h2>פרטי משלוח</h2>
            <Box 
                className="checkout-customer-details" 
                component="form" 
                sx={{ display: 'flex', flexDirection: 'column', gap: 2, width: '100%' }}
            >
                <TextField
                    name="name"
                    label="שם מלא"
                    variant="standard"
                    value={formData.name}
                    onChange={handleInputChange}
                    fullWidth
                    sx={textFieldStyles}
                />
                <TextField
                    name="phone"
                    label="טלפון"
                    variant="standard"
                    value={formData.phone}
                    onChange={handleInputChange}
                    fullWidth
                    sx={textFieldStyles}
                />
                <TextField
                    name="email"
                    label="דואר אלקטרוני"
                    variant="standard"
                    value={formData.email}
                    onChange={handleInputChange}
                    fullWidth
                    sx={textFieldStyles}
                />
                <TextField
                    name="city"
                    label="עיר"
                    variant="standard"
                    value={formData.city}
                    onChange={handleInputChange}
                    fullWidth
                    sx={textFieldStyles}
                />
                <TextField
                    name="address"
                    label="כתובת"
                    variant="standard"
                    value={formData.address}
                    onChange={handleInputChange}
                    fullWidth
                    sx={textFieldStyles}
                />
                <TextField
                    name="zipCode"
                    label="מיקוד"
                    variant="standard"
                    value={formData.zipCode}
                    onChange={handleInputChange}
                    fullWidth
                    sx={textFieldStyles}
                />
                <TextField
                    name="instructions"
                    label="הוראות מיוחדות"
                    variant="standard"
                    value={formData.instructions}
                    onChange={handleInputChange}
                    multiline
                    rows={4}
                    fullWidth
                    sx={textFieldStyles}
                />
            </Box>
            <div className="checkout-button-container">
    <Button
        className="checkout-button"
        onClick={handleFormSubmit}
        disabled={isCartEmpty}
        variant="contained"
        color="primary"
    >
        המשך לתשלום
    </Button>
</div>

        </div>
    );
};

export default Checkout;
