import React, { useState } from 'react';
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";
import { TextField, Button, Box } from '@mui/material';
import lock_icon from '../assets/frontend_assets/lock-icon.png';
import './Checkout.css';
import DeliveryInfo from '../deliveryInfo/DeliveryInfo';

const Checkout = ({ totalCartValue, isCartEmpty, navigateToOrder }) => {
    const [paymentStatus, setPaymentStatus] = useState(null);

    const totalPayment = (totalCartValue) => totalCartValue + 35;

    const handlePaymentSuccess = (details) => {
        console.log('Payment Successful:', details);
        setPaymentStatus("success");
        // Call navigateToOrder or any function to handle order completion
    };

    const handlePaymentError = (err) => {
        console.error('Payment Failed:', err);
        setPaymentStatus("error");
    };

    return (
        <div className="checkout-section">
            <h2>פרטי משלוח</h2>
            <DeliveryInfo />

            <div className="checkout-total">
                <div>
                    <span>סה"כ מחיר בעגלה:</span>
                    <span>₪{totalCartValue}</span>
                </div>
                <div>
                    <span>סה"כ מחיר משלוח:</span>
                    <span>₪35.00</span>
                </div>
                <div className="checkout-total-payment">
                    <span>סה"כ לתשלום:</span>
                    <span>₪{totalPayment(totalCartValue)}</span>
                </div>
            </div>

            <div className="checkout-button-container">
                <PayPalScriptProvider options={{ "client-id": "Aaj4iB9K3iSqhCt5suzrRxfiXOMjnZkEitBR-GOBJVIEFuIrLKxZETZILv6MClHS_efn2sI-phA6nQSe" }}>
                    <PayPalButtons
                        style={{ layout: "vertical" }}
                        createOrder={(data, actions) => {
                            return actions.order.create({
                                purchase_units: [{
                                    amount: {
                                        value: totalPayment(totalCartValue).toFixed(2), // Convert total to string
                                    },
                                }],
                            });
                        }}
                        onApprove={(data, actions) => {
                            return actions.order.capture().then(handlePaymentSuccess);
                        }}
                        onError={handlePaymentError}
                    />
                </PayPalScriptProvider>
            </div>
        </div>
    );
};

export default Checkout;
