import React, { useState } from 'react';
import { TextField, Button, Box } from '@mui/material';
import './Checkout.css';

const Checkout = ({ totalCartValue, isCartEmpty, navigateToOrder }) => {
    const [formData, setFormData] = useState({
        name: '',
        phone: '',
        email: '',
        city: '',
        address: '',
        zipCode: '',
        instructions: '',
    });

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleFormSubmit = () => {
        if (!isCartEmpty) {
            console.log('User Delivery Details:', formData);
            navigateToOrder();
        }
    };

    return (
        <div className="checkout-section">
            <h2>פרטי משלוח</h2>
            <Box
                className="checkout-customer-details"
                component="form"
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    gap: 2,
                    width: '100%',
                }}
            >
                {['name', 'phone', 'email', 'city', 'address', 'zipCode'].map((field) => (
                    <TextField
                        key={field}
                        name={field}
                        label={
                            field === 'name'
                                ? 'שם מלא'
                                : field === 'phone'
                                ? 'טלפון'
                                : field === 'email'
                                ? 'דואר אלקטרוני'
                                : field === 'city'
                                ? 'עיר'
                                : field === 'address'
                                ? 'כתובת'
                                : 'מיקוד'
                        }
                        variant="standard"
                        value={formData[field]}
                        onChange={handleInputChange}
                        fullWidth
                        InputLabelProps={{
                            style: { textAlign: 'right', right: 0 },
                            shrink: true,
                        }}
                        inputProps={{
                            style: { textAlign: 'right' },
                        }}
                    />
                ))}
                <TextField
                    name="instructions"
                    label="הוראות מיוחדות"
                    variant="standard"
                    value={formData.instructions}
                    onChange={handleInputChange}
                    fullWidth
                    multiline
                    rows={4}
                    InputLabelProps={{
                        style: { textAlign: 'right', right: 0 },
                        shrink: true,
                    }}
                    inputProps={{
                        style: { textAlign: 'right' },
                    }}
                />
            </Box>
            <Button
                className="checkout-button"
                onClick={handleFormSubmit}
                disabled={isCartEmpty}
                variant="contained"
                color="primary"
                sx={{ marginTop: 2 }}
            >
                המשך לתשלום
            </Button>
        </div>
    );
};

export default Checkout;
