import React, { useContext, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './Navbar.css';
import logo from '../assets/frontend_assets/PetShop3.png';
import cart_icon from '../assets/frontend_assets/cart-icon.png';
import user_icon from '../assets/frontend_assets/user_account.png';
import { ShopContext } from '../../context/ShopContext';

const Navbar = () => {
    const [menu, setMenu] = useState("shop");
    const location = useLocation();
    const navigate = useNavigate(); // Hook for navigation

    // Only apply the banner class on the home page
    const bannerClass = location.pathname === '/' ? 'banner' : '';
    const { getTotalItems } = useContext(ShopContext);

    return (
        <div className={bannerClass}>
            <div className='navbar'>
                <div className="nav-logo">
                    <img src={logo} alt="Pet Shop" />
                </div>
                <div className="nav-cart-user">
                    <ul className="nav-menu">
                        <li
                            onClick={() => {
                                setMenu("shop");
                                navigate('/'); // Navigate to home
                            }}
                            style={{ cursor: 'pointer', color: menu === "shop" ? "red" : "black" }}
                        >
                            חנות
                        </li>
                        <li
                            onClick={() => {
                                setMenu("dog");
                                navigate('/dog'); // Navigate to dog
                            }}
                            style={{ cursor: 'pointer', color: menu === "dog" ? "red" : "black" }}
                        >
                            כלבים
                        </li>
                        <li
                            onClick={() => {
                                setMenu("cat");
                                navigate('/cat'); // Navigate to cat
                            }}
                            style={{ cursor: 'pointer', color: menu === "cat" ? "red" : "black" }}
                        >
                            חתולים
                        </li>
                        <li
                            onClick={() => {
                                setMenu("bird");
                                navigate('/bird'); // Navigate to bird
                            }}
                            style={{ cursor: 'pointer', color: menu === "bird" ? "red" : "black" }}
                        >
                            ציפורים
                        </li>
                    </ul>
                    <img src={user_icon} alt="User" />
                    <div className="cart-count">{getTotalItems()}</div>
                    <img
                        src={cart_icon}
                        alt="Cart"
                        style={{ cursor: 'pointer' }}
                        onClick={() => navigate('/cart')} // Navigate to cart
                    />
                </div>
            </div>
        </div>
    );
};

export default Navbar;
