import React, { useState, useEffect } from 'react';
import { TextField, Button, Box } from '@mui/material';
import './Checkout.css';

const Checkout = ({ totalCartValue, isCartEmpty, navigateToOrder }) => {
    const [formData, setFormData] = useState({
        name: '',
        phone: '',
        email: '',
        city: '',
        address: '',
        zipCode: '',
        instructions: '',
    });

    const totalPayment = (totalCartValue) => totalCartValue + 35;

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleFormSubmit = () => {
        if (!isCartEmpty) {
            console.log('User Delivery Details:', formData);
            navigateToOrder();
        }
    };

    const loadPayPalScript = () => {
        const script = document.createElement('script');
        script.src = 'https://www.paypal.com/sdk/js?client-id=YOUR_PAYPAL_CLIENT_ID&currency=ILS';
        script.async = true;
        script.onload = () => window.paypal && initializePayPalButton();
        document.body.appendChild(script);
    };

    const initializePayPalButton = () => {
        window.paypal.Buttons({
            createOrder: (data, actions) => {
                return actions.order.create({
                    purchase_units: [
                        {
                            amount: {
                                value: totalPayment(totalCartValue).toFixed(2), // Pass the total payment amount
                            },
                        },
                    ],
                });
            },
            onApprove: (data, actions) => {
                return actions.order.capture().then((details) => {
                    console.log('Payment Successful!', details);
                    navigateToOrder();
                });
            },
            onError: (err) => {
                console.error('PayPal Checkout Error:', err);
            },
        }).render('#paypal-button-container');
    };

    useEffect(() => {
        loadPayPalScript();
    }, []);

    const textFieldStyles = {
        '& .MuiInputLabel-root': {
            textAlign: 'right',
            right: 0,
            transformOrigin: 'top right',
        },
        '& .MuiInputBase-input': {
            textAlign: 'right',
        },
    };

    return (
        <div className="checkout-section">
            <h2>פרטי משלוח</h2>
            <Box 
                className="checkout-customer-details" 
                component="form" 
                sx={{ display: 'flex', flexDirection: 'column', gap: 2, width: '80%', margin: '0 auto', mb: 10 }}
            >
                {Object.keys(formData).map((field) => (
                    <TextField
                        key={field}
                        name={field}
                        label={field === 'zipCode' ? 'מיקוד' : field === 'instructions' ? 'הוראות' : field}
                        variant="standard"
                        value={formData[field]}
                        onChange={handleInputChange}
                        multiline={field === 'instructions'}
                        rows={field === 'instructions' ? 4 : 1}
                        fullWidth
                        sx={textFieldStyles}
                    />
                ))}
            </Box>

            <div className="checkout-total">
                <div>
                    <span>סה"כ מחיר בעגלה:</span>
                    <span>₪{totalCartValue}</span>
                </div>
                <div>
                    <span>סה"כ מחיר משלוח:</span>
                    <span>₪35.00</span>
                </div>
                <div className="checkout-total-payment">
                    <span>סה"כ לתשלום:</span>
                    <span>₪{totalPayment(totalCartValue)}</span>
                </div>
            </div>

            <div className="checkout-button-container">
                <Button
                    className="checkout-button"
                    disabled={isCartEmpty}
                    variant="contained"
                    color="primary"
                    onClick={handleFormSubmit}
                >
                    שמירת פרטים
                </Button>
            </div>

            <div id="paypal-button-container" style={{ marginTop: '20px' }}></div>
        </div>
    );
};

export default Checkout;
