import React, { useState } from "react";
import "./OrderPage.css";

const OrderPage = () => {
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        phone: "",
        address: "",
        city: "",
        zip: "",
    });

    const [paymentMethod, setPaymentMethod] = useState("credit_card");

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("Order submitted:", formData, paymentMethod);
        // You can replace this with API call logic for placing the order
    };

    return (
        <div className="order-page">
            <h1>ביצוע הזמנה</h1>
            <form onSubmit={handleSubmit}>
                {/* Contact Information */}
                <section className="order-section">
                    <h2>פרטי קשר</h2>
                    <label>
                        שם:
                        <input
                            type="text"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            required
                        />
                    </label>
                    <label>
                        אימייל:
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />
                    </label>
                    <label>
                        טלפון:
                        <input
                            type="tel"
                            name="phone"
                            value={formData.phone}
                            onChange={handleChange}
                            required
                        />
                    </label>
                    <label>
                        כתובת:
                        <input
                            type="text"
                            name="address"
                            value={formData.address}
                            onChange={handleChange}
                            required
                        />
                    </label>
                    <label>
                        עיר:
                        <input
                            type="text"
                            name="city"
                            value={formData.city}
                            onChange={handleChange}
                            required
                        />
                    </label>
                    <label>
                        מיקוד:
                        <input
                            type="text"
                            name="zip"
                            value={formData.zip}
                            onChange={handleChange}
                            required
                        />
                    </label>
                </section>

                {/* Payment Information */}
                <section className="order-section">
                    <h2>אמצעי תשלום</h2>
                    <label>
                        <input
                            type="radio"
                            name="paymentMethod"
                            value="credit_card"
                            checked={paymentMethod === "credit_card"}
                            onChange={(e) => setPaymentMethod(e.target.value)}
                        />
                        כרטיס אשראי
                    </label>
                    <label>
                        <input
                            type="radio"
                            name="paymentMethod"
                            value="paypal"
                            checked={paymentMethod === "paypal"}
                            onChange={(e) => setPaymentMethod(e.target.value)}
                        />
                        PayPal
                    </label>
                </section>

                <button type="submit" className="order-submit-button">
                    בצע הזמנה
                </button>
            </form>
        </div>
    );
};

export default OrderPage;
