import React, { useContext, useState, useEffect } from "react";
import { ShopContext } from "../context/ShopContext";
import "./css/Order.css";

const Order = () => {
    const { cartItems, all_products, getTotalCartValue, clearItemFromCart } = useContext(ShopContext);
    const [orderDetails, setOrderDetails] = useState({
        name: "",
        phone: "",
        address: "",
        email: "",
        city: "",
        zip: "",
    });

    const [sdkReady, setSdkReady] = useState(false); // Track if the PayPal SDK is loaded
    const cartProducts = all_products.filter((product) => cartItems[product.id] > 0);
    const totalPrice = getTotalCartValue();

    // Load PayPal SDK
    useEffect(() => {
        const addPayPalScript = async () => {
            const script = document.createElement("script");
            script.src = `https://www.paypal.com/sdk/js?client-id=_ID&currency=ILS`;
            script.type = "text/javascript";
            script.async = true;
            script.onload = () => setSdkReady(true);
            document.body.appendChild(script);
        };

        if (!window.paypal) {
            addPayPalScript();
        } else {
            setSdkReady(true);
        }
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setOrderDetails((prevDetails) => ({
            ...prevDetails,
            [name]: value,
        }));
    };

    const handlePlaceOrder = () => {
        if (
            !orderDetails.name ||
            !orderDetails.phone ||
            !orderDetails.address ||
            !orderDetails.email ||
            !orderDetails.city ||
            !orderDetails.zip
        ) {
            alert("Please fill out all the required fields!");
            return;
        }

        console.log("Order Placed:", { orderDetails, cartProducts, totalPrice });
    };

    // PayPal Button Configuration
    const renderPayPalButton = () => {
        if (sdkReady) {
            window.paypal
                .Buttons({
                    createOrder: (data, actions) => {
                        return actions.order.create({
                            purchase_units: [
                                {
                                    amount: {
                                        value: totalPrice.toFixed(2), // Total price in ILS
                                        currency_code: "ILS",
                                    },
                                },
                            ],
                        });
                    },
                    onApprove: (data, actions) => {
                        return actions.order.capture().then((details) => {
                            alert(`Payment successful! Thank you, ${details.payer.name.given_name}.`);
                            clearItemFromCart(); // Clear cart
                            console.log("Payment Details:", details);
                        });
                    },
                    onError: (err) => {
                        console.error("PayPal Checkout Error:", err);
                        alert("Something went wrong with the payment.");
                    },
                })
                .render("#paypal-button-container");
        }
    };

    useEffect(() => {
        if (sdkReady) {
            renderPayPalButton();
        }
    }, [sdkReady]);

    return (
        <div className="order-page">
            <h2>Complete Your Order</h2>

            {/* Contact Information Form */}
            <div className="contact-form">
                <h3>Contact Information</h3>
                <label>
                    Name:
                    <input
                        type="text"
                        name="name"
                        value={orderDetails.name}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Phone:
                    <input
                        type="tel"
                        name="phone"
                        value={orderDetails.phone}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Email:
                    <input
                        type="email"
                        name="email"
                        value={orderDetails.email}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    City:
                    <input
                        type="text"
                        name="city"
                        value={orderDetails.city}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Zip Code:
                    <input
                        type="text"
                        name="zip"
                        value={orderDetails.zip}
                        onChange={handleChange}
                        required
                    />
                </label>
                <label>
                    Address:
                    <textarea
                        name="address"
                        value={orderDetails.address}
                        onChange={handleChange}
                        required
                    />
                </label>
            </div>

            {/* Order Summary */}
            <div className="order-summary">
                <h3>Order Summary</h3>
                {cartProducts.map((product) => (
                    <div key={product.id} className="summary-item">
                        <p>
                            {product.name} x {cartItems[product.id]}
                        </p>
                        <p>₪{(product.price * cartItems[product.id]).toFixed(2)}</p>
                    </div>
                ))}
                <p className="total">Total: ₪{totalPrice.toFixed(2)}</p>
            </div>

            {/* Payment Section */}
            <div className="payment-section">
                <h3>Payment</h3>
                {sdkReady ? (
                    <div id="paypal-button-container"></div>
                ) : (
                    <p>Loading PayPal buttons...</p>
                )}
            </div>
        </div>
    );
};

export default Order;
