import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Navbar.css'; // Import your Navbar styles

const Navbar = () => {
  const navigate = useNavigate();

  const handleNavigate = (category, type) => {
    navigate(`/shopcategory?category=${category}&type=${type}`);
  };

  return (
    <nav className="navbar">
      <div className="nav-logo">
        <img src="/path-to-logo" alt="Logo" />
      </div>
      <ul className="nav-menu">
        <li>
          כלבים
          <ul className="submenu">
            <li onClick={() => handleNavigate('dog', 'food')}>אוכל לכלבים</li>
            <li onClick={() => handleNavigate('dog', 'accessories')}>ציוד לכלבים</li>
          </ul>
        </li>
        <li>
          חתולים
          <ul className="submenu">
            <li onClick={() => handleNavigate('cat', 'food')}>אוכל לחתולים</li>
            <li onClick={() => handleNavigate('cat', 'accessories')}>ציוד לחתולים</li>
          </ul>
        </li>
        <li>
          ציפורים
          <ul className="submenu">
            <li onClick={() => handleNavigate('bird', 'food')}>אוכל לציפורים</li>
            <li onClick={() => handleNavigate('bird', 'cages')}>כלובים</li>
          </ul>
        </li>
      </ul>
    </nav>
  );
};

export default Navbar;



