import React, { createContext, useEffect, useState } from 'react';
// import all_products from '../components/assets/frontend_assets/all_products';

export const ShopContext = createContext(null);

const getDefaultCart = () => {
    let cart = {};
    for (let i = 0; i < 300 + 1; i++) {
        cart[i] = 0;
    }
    return cart;
};

const ShopContextProvider = (props) => {
    const [allProducts, setAllProducts] = useState([]);
    const [cartItems, setCartItems] = useState(getDefaultCart());

    useEffect(()=> {
        fetch('http://localhost:4000/allproducts')
        .then((res)=> res.json()).then((data)=>setAllProducts(data))
    },[])

    const addToCart = (itemId, quantity = 1) => {
        setCartItems((prev) => ({ ...prev, [itemId]: prev[itemId] + quantity }));
    };

    const removeFromCart = (itemId) => {
        setCartItems((prev) => ({ ...prev, [itemId]: Math.max(prev[itemId] - 1, 0) }));
    };

    const updateCartQuantity = (itemId, newQuantity) => {
        setCartItems((prev) => ({ ...prev, [itemId]: newQuantity }));
    };

    const clearItemFromCart = (itemId) => {
        setCartItems((prev) => ({ ...prev, [itemId]: 0 }));
    };

    // Calculate total cart value
    const getTotalCartValue = () => {
        return all_products.reduce((total, product) => {
            const quantity = cartItems[product.id] || 0;
            return total + product.price * quantity;
        }, 0);
    };

    // Calculate total items in the cart
    const getTotalItems = () => {
        return all_products.reduce((total, product) => {
            return total + (cartItems[product.id] || 0);
        }, 0);
    };

    const contextValue = {
        all_products,
        cartItems,
        addToCart,
        removeFromCart,
        updateCartQuantity,
        clearItemFromCart,
        getTotalCartValue,
        getTotalItems,
    };

    return (
        <ShopContext.Provider value={contextValue}>
            {props.children}
        </ShopContext.Provider>
    );
};

export default ShopContextProvider;
